        setUpper("yes triggered");
    }
    const [Upper, setUpper] = useState('Enter');
    return (
        <div>
            <div className="container">
                <div className="form-floating my-3 mb-3">
                    <h1>{props.heading}</h1>
                    <textarea id="textUpper" value={Upper} onChange={textChange} name="textUpper" rows="8" cols="100" />
                </div>
                <button type="button" onClick={handleOnClick} className="btn btn-primary">Text to UpperCase</button>
            </div>
        </div>
    )
}
